package dpp

import (
	"testing"

	"github.com/libsv/go-bc/spv"
	"github.com/matryer/is"
)

func TestPaymentCreate_Validate(t *testing.T) {
	is := is.New(t)
	tests := map[string]struct {
		req Payment
		exp string
	}{
		"valid request should return no errors": {
			req: Payment{
				MerchantData: Merchant{
					ExtendedData: map[string]interface{}{
						"paymentReference": "abc123",
					},
				},
				ProofCallbacks: map[string]ProofCallback{
					"http://myproofs/proofs": {
						Token: "abc123",
					},
				},
				SPVEnvelope: &spv.Envelope{
					TxID:          "a4c76f8a7c05a91dcf5699b95b54e856298e50c1ceca9a8a5569c8532c500c11",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				},
				Memo: "test this please",
			},
			exp: "no validation errors",
		}, "transaction with invalid txID should error": {
			req: Payment{
				SPVEnvelope: &spv.Envelope{
					TxID:          "0x74657374696e672074657374696e67",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				},
				MerchantData: Merchant{
					ExtendedData: map[string]interface{}{
						"paymentReference": "abc123",
					},
				},
				Memo: "test this please",
			},
			exp: "[spvEnvelope.rawTx: transaction mismatch, root txId does not match rawTx supplied], [spvEnvelope.txId: value should be exactly 64 characters]",
		}, "transaction with invalid hex should error": {

			req: Payment{
				MerchantData: Merchant{
					ExtendedData: map[string]interface{}{
						"paymentReference": "abc123",
					},
				},
				SPVEnvelope: &spv.Envelope{
					TxID:          "0x74657374696e672074657374696e67",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				},
				Memo: "test this please",
			},
			exp: "[spvEnvelope.rawTx: invalid rawTx hex supplied: encoding/hex: odd length hex string], [spvEnvelope.txId: value should be exactly 64 characters]",
		}, "merchant data missing extended data should error": {
			req: Payment{
				SPVEnvelope: &spv.Envelope{
					TxID:          "a4c76f8a7c05a91dcf5699b95b54e856298e50c1ceca9a8a5569c8532c500c11",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				},
				Memo: "test this please",
			},
			exp: "[merchantData.extendedData: value cannot be empty]",
		}, "merchant data missing payment reference should error": {
			req: Payment{
				SPVEnvelope: &spv.Envelope{
					TxID:          "a4c76f8a7c05a91dcf5699b95b54e856298e50c1ceca9a8a5569c8532c500c11",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				},
				MerchantData: Merchant{ExtendedData: map[string]interface{}{"test": "value"}},
				Memo:         "test this please",
			},
			exp: "[merchantData.paymentReference: value cannot be empty]",
		}, "refundTo too long should error": {
			req: Payment{
				SPVEnvelope: &spv.Envelope{
					TxID:          "a4c76f8a7c05a91dcf5699b95b54e856298e50c1ceca9a8a5569c8532c500c11",
					RawTx:         "",
					Proof:         nil,
					MapiResponses: nil,
					Parents:       nil,
				}, RefundTo: func() *string {
					bb := make([]byte, 0)
					// generate string 1 more byte than 10000
					for i := 0; i <= 100; i++ {
						bb = append(bb, 42)
					}
					out := string(bb)
					return &out
				}(),
				MerchantData: Merchant{
					ExtendedData: map[string]interface{}{
						"paymentReference": "abc123",
					},
				},
				Memo: "test this please",
			},
			exp: "[refundTo: value must be between 0 and 100 characters]",
		},
	}
	for name, test := range tests {
		t.Run(name, func(t *testing.T) {
			is.NewRelaxed(t)
			v := test.req.Validate()
			if test.exp == "no validation errors" {
				is.NoErr(v)
				return
			}
			is.Equal(test.exp, v.Error())
		})
	}
}
